const Icon = ({ name, className }: IconProps) => {
    let icon;

    switch (name) {
        // socials
        case 'discord':
            icon = <Discord />;
            break;
        case 'spotify':
            icon = <Spotify />;
            break;
        case 'github':
            icon = <Github />;
            break;
        // skills
        case 'typescript':
            icon = <Typescript />;
            break;
        case 'javascript':
            icon = <Javascript />;
            break;
        case 'sass':
            icon = <Sass />;
            break;
        case 'html':
            icon = <Html />;
            break;
        case 'css':
            icon = <Css />;
            break;
        case 'nodejs':
            icon = <NodeJS />;
            break;
        case 'reactjs':
            icon = <ReactJS />;
            break;
        case 'nextjs':
            icon = <NextJS />;
            break;
        case 'csharp':
            icon = <CSharp />;
            break;
        case 'dotnet':
            icon = <DotNet />;
            break;
        case 'mysql':
            icon = <MySQL />;
            break;
        case 'sqlite':
            icon = <SQLite />;
            break;
        // tools
        case 'code':
            icon = <Code />;
            break;
        case 'visualstudio':
            icon = <VisualStudio />;
            break;
        case 'git':
            icon = <Git />;
            break;
    }

    return <div className={className}>{icon}</div>;
};

export default Icon;
